import React from 'react';
import './PriorityOverview.css';

const PriorityOverview = ({ tasks }) => {
  const countTasksByPriority = (priority) => {
    return tasks.filter((task) => task.priority === priority).length;
  };

  return (
    <div className="priority-sections">
      <div className="priority-section high-priority">
        <h3>High Priority</h3>
        <p>{countTasksByPriority('High')} tasks</p>
      </div>
      <div className="priority-section medium-priority">
        <h3>Medium Priority</h3>
        <p>{countTasksByPriority('Medium')} tasks</p>
      </div>
      <div className="priority-section low-priority">
        <h3>Low Priority</h3>
        <p>{countTasksByPriority('Low')} tasks</p>
      </div>
    </div>
  );
};

export default PriorityOverview;
