import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext'; // Assuming AuthContext exists
import './Navbar.css'; // Add your sidebar styles here

function Navbar() {
  const { isLoggedIn } = useContext(AuthContext); // Check authentication status

  if (!isLoggedIn) return null; // Render nothing if the user is not authenticated

  return (
    <nav className="sidebar">
      <Link to="/">Home</Link>
      <Link to="/tasks">Tasks</Link>
      <Link to="/create">Create Task</Link>
      <Link to="/archive">Archive</Link>
    </nav>
  );
}

export default Navbar;
