import React, { useState, useContext } from 'react';
import axios from 'axios';
import './CSS/Homepage.css'; // Add styles for the dashboard layout
import CreateTask from '../components/CreateTask/CreateTask';
import TaskList from '../components/TaskList/TaskList';
import Archive from '../components/Archive';
import { AuthContext } from '../context/AuthContext'; // Assuming you have AuthContext

function Homepage() {
  const { user } = useContext(AuthContext); // Get the user from AuthContext
  const [activeSection, setActiveSection] = useState('dashboard'); // Manage active section

  const [tasks, setTasks] = useState([]); // Store tasks in state

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/tasks');
      setTasks(response.data.filter((task) => !task.archived)); // Exclude archived tasks
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Define the onSubmit function for CreateTask
  const handleTaskSubmit = async (taskData) => {
    try {
      console.log('Task Submitted:', taskData);

      const response = await axios.post('http://localhost:3001/api/tasks', taskData);

      if (response.status === 201) {
        console.log('Task created:', response.data);
        fetchTasks(); // Refresh the task list
        setActiveSection('tasks'); // Navigate to TaskList section
      }
    } catch (error) {
      console.error('Error creating task:', error.response?.data || error.message); // Improved logging
    }
  };

  // Define the onClose function for CreateTask
  const handleCloseCreateTask = () => {
    setActiveSection('dashboard'); // Return to the dashboard if the modal is closed
  };

  return (
    <div className="homepage-container">
      {/* Sidebar */}
      <div className="sidebar">
        {/* User Section */}
        <div className="user-section">
          <p>Hello,</p>
          <p><strong>{user ? user.name : 'Guest'}!</strong></p>
        </div>

        <div
          className={`widget ${activeSection === 'tasks' ? 'active' : ''}`}
          onClick={() => {
            setActiveSection('tasks');
            fetchTasks(); // Fetch tasks when navigating to the TaskList
          }}
        >
          <h3>Tasks</h3>
          <p>View and manage your tasks.</p>
        </div>
        <div
          className={`widget add-task-widget ${activeSection === 'createTask' ? 'active' : ''}`}
          onClick={() => setActiveSection('createTask')}
        >
          <h3>
            <span className="add-symbol">+</span> Create Task
          </h3>
          <p>Add new tasks to your list.</p>
        </div>
        <div
          className={`widget ${activeSection === 'archive' ? 'active' : ''}`}
          onClick={() => setActiveSection('archive')}
        >
          <h3>Archive</h3>
          <p>Access archived tasks.</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeSection === 'dashboard' && (
          <>
            <h1>Welcome to the Dashboard</h1>
            <p>Here you can manage all your tasks effectively.</p>
          </>
        )}
        {activeSection === 'tasks' && <TaskList tasks={tasks} fetchTasks={fetchTasks} />}
        {activeSection === 'createTask' && (
          <>
            <h2>Create a New Task</h2>
            <CreateTask
              onSubmit={handleTaskSubmit}
              onClose={handleCloseCreateTask}
            />
          </>
        )}
        {activeSection === 'archive' && <Archive />}
      </div>
    </div>
  );
}

export default Homepage;
