import React, { useState } from 'react';
import './CSS/Homepage.css'; // Add styles for the dashboard layout
import CreateTask from '../components/CreateTask/CreateTask';
import Navbar from '../components/Navbar';

function Homepage() {
    const [showCreateTask, setShowCreateTask] = useState(false); // Manage visibility of TaskList

    return (
      <div className="homepage-container">
        {/* Sidebar */}
        <div className="sidebar">
          <h2>Task Manager</h2>
          <div className="widget">
            <h3>Tasks</h3>
            <p>View and manage your tasks.</p>
          </div>
          <div className="widget add-task-widget" onClick={() => setShowCreateTask(true)}>
            <h3>
              <span className="add-symbol">+</span> Create Task
            </h3>
            <p>Add new tasks to your list.</p>
          </div>
          <div className="widget">
            <h3>Archive</h3>
            <p>Access archived tasks.</p>
          </div>
        </div>

        <div className="">

        </div>

      </div>
    );
}

export default Homepage;
