import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TaskList() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/api/tasks').then((response) => {
      setTasks(response.data);
    });
  }, []);

    // Toggle task completion
  const toggleCompletion = (task) => {
    updateTask(task.id, { status: task.status === 'Completed' ? 'To-Do' : 'Completed' });
  };

  // Edit priority
  const changePriority = (task, newPriority) => {
    updateTask(task.id, { priority: newPriority });
  };

  // Edit deadline
  const changeDeadline = (task, newDeadline) => {
    updateTask(task.id, { deadline: newDeadline });
  };

  return (
    <div>
      <h2>Task List</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
            <p>
              Priority: 
              <select
                value={task.priority}
                onChange={(e) => changePriority(task, e.target.value)}
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
            </p>
            <p>
              Status: 
              <button onClick={() => toggleCompletion(task)}>
                {task.status === 'Completed' ? 'Mark as To-Do' : 'Mark as Completed'}
              </button>
            </p>
            <p>
              Deadline: 
              <input
                type="date"
                value={task.deadline || ''}
                onChange={(e) => changeDeadline(task, e.target.value)}
              />
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskList;
