import React, { useState } from 'react';
import './CSS/Homepage.css'; // Add styles for the dashboard layout
import CreateTask from '../components/CreateTask/CreateTask';
import TaskList from '../components/TaskList';
import Archive from '../components/Archive';

function Homepage() {
  // State to manage which section to display
  const [activeSection, setActiveSection] = useState('dashboard'); // Default to "dashboard"

  return (
    <div className="homepage-container">
      {/* Sidebar */}
      <div className="sidebar">

        <div
          className="widget"
          onClick={() => setActiveSection('tasks')} // Set active section to "tasks"
        >
          <h3>Tasks</h3>
          <p>View and manage your tasks.</p>
        </div>

        <div
          className="widget add-task-widget"
          onClick={() => setActiveSection('createTask')} // Set active section to "createTask"
        >
          <h3>
            <span className="add-symbol">+</span> Create Task
          </h3>
          <p>Add new tasks to your list.</p>
        </div>

        <div
          className="widget"
          onClick={() => setActiveSection('archive')} // Set active section to "archive"
        >
          <h3>Archive</h3>
          <p>Access archived tasks.</p>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeSection === 'dashboard' && (
          <>
            <h1>Welcome to the Dashboard</h1>
            <p>Here you can manage all your tasks effectively.</p>
          </>
        )}
        {activeSection === 'tasks' && <TaskList />} {/* Render TaskList */}
        {activeSection === 'createTask' && <CreateTask />} {/* Render CreateTask */}
        {activeSection === 'archive' && <Archive />} {/* Render Archive */}
      </div>
    </div>
  );
}

export default Homepage;
