import React, { useState, useContext, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CSS/Homepage.css'; // Add styles for the dashboard layout
import CreateTask from '../components/CreateTask/CreateTask';
import TaskList from '../components/TaskList/TaskList';
import Archive from '../components/Archive/Archive';
import PriorityOverview from '../components/PriorityOverview/PriorityOverview';
import { AuthContext } from '../context/AuthContext'; // Assuming you have AuthContext

function Homepage() {
  const { user, logout } = useContext(AuthContext); // Get the user from AuthContext
  const [activeSection, setActiveSection] = useState('dashboard'); // Manage active section
  const [isCreateTaskOpen, setIsCreateTaskOpen] = useState(false); // Track Create Task modal visibility
  const [tasks, setTasks] = useState([]); // Store tasks in state
  const navigate = useNavigate();

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/tasks');
      setTasks(response.data.filter((task) => !task.archived)); // Exclude archived tasks
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    // Fetch tasks when the page loads
    if (activeSection === 'dashboard' || activeSection === 'tasks') {
      fetchTasks();
    }
  }, [activeSection]);

  // Define the onSubmit function for CreateTask
  const handleTaskSubmit = async (taskData) => {
    try {
      const response = await axios.post('http://localhost:3001/api/tasks', taskData);
      if (response.status === 201) {
        console.log('Task created:', response.data);
        fetchTasks(); // Refresh the task list
        setIsCreateTaskOpen(false); // Close Create Task modal
      }
    } catch (error) {
      console.error('Error creating task:', error.response?.data || error.message);
    }
  };

  // Define the onClose function for CreateTask
  const handleCloseCreateTask = () => {
    setIsCreateTaskOpen(false); // Close the Create Task modal
  };

  const handleSignOut = () => {
    logout(); // Call logout function from AuthContext
    navigate('/login'); // Redirect to login page
  };

  return (
    <div className="homepage-container">
      {/* Sidebar */}
      <div className="sidebar">
        {/* User Section */}
        <div className="user-section">
          <p>Hello,</p>
          <p><strong>{user ? user.first_name : 'Guest'}!</strong></p>
        </div>

        {/* Dashboard Widget */}
        <div
          className={`widget ${activeSection === 'dashboard' ? 'active' : ''}`}
          onClick={() => setActiveSection('dashboard')}
        >
          <h3>Dashboard</h3>
          <p>Back to the welcome and overview page.</p>
        </div>

        {/* Tasks Widget */}
        <div
          className={`widget ${activeSection === 'tasks' ? 'active' : ''}`}
          onClick={() => setActiveSection('tasks')}
        >
          <h3>Tasks</h3>
          <p>View and manage your tasks.</p>
        </div>

        {/* Create Task Widget */}
        <div
          className="widget add-task-widget"
          onClick={() => setIsCreateTaskOpen(true)} // Open the Create Task modal
        >
          <h3>
            <span className="add-symbol">+</span> Create Task
          </h3>
          <p>Add new tasks to your list.</p>
        </div>

        {/* Archive Widget */}
        <div
          className={`widget ${activeSection === 'archive' ? 'active' : ''}`}
          onClick={() => setActiveSection('archive')}
        >
          <h3>Archive</h3>
          <p>Access archived tasks.</p>
        </div>

        {/* Add the Signet button */}
        <div className="signet-button-container">
            <button className="signout-button" onClick={handleSignOut}>
                Sign out
            </button>
        </div>

      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeSection === 'dashboard' && (
          <>
            <h1>Welcome to Taskify</h1>
            <p>Here you can manage all your tasks effectively.</p>
            <PriorityOverview tasks={tasks} />
          </>
        )}
        {activeSection === 'tasks' && <TaskList tasks={tasks} fetchTasks={fetchTasks} />}
        {activeSection === 'archive' && <Archive />}
      </div>

      {/* Create Task Modal */}
      {isCreateTaskOpen && (
        <div className="popup-overlay">
          <div className="popup-content">
            <h3>Create a New Task</h3>
            <CreateTask
              onSubmit={handleTaskSubmit}
              onClose={handleCloseCreateTask}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Homepage;
