import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TaskList.css'; // Import the CSS file

function TaskList() {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the backend
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/tasks');
      setTasks(response.data.filter((task) => !task.archived)); // Exclude archived tasks
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Archive a task
  const archiveTask = async (id) => {
    try {
      await axios.put(`http://localhost:3001/api/tasks/${id}`, { archived: true });
      console.log(`Task ${id} archived successfully.`);
      fetchTasks(); // Refresh tasks after archiving
    } catch (error) {
      console.error('Error archiving task:', error);
    }
  };

  // Delete a task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/tasks/${id}`);
      console.log(`Task ${id} deleted successfully.`);
      fetchTasks(); // Refresh tasks after deletion
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div className="task-list-container">
      {tasks.map((task) => (
        <div className="task-card" key={task.id}>
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <p>
            Priority: 
            <select
              value={task.priority}
              onChange={(e) =>
                axios.put(`http://localhost:3001/api/tasks/${task.id}`, { priority: e.target.value }).then(fetchTasks)
              }
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
          </p>
          <p>
            Status:
            <select
              value={task.status}
              onChange={(e) =>
                axios.put(`http://localhost:3001/api/tasks/${task.id}`, { status: e.target.value }).then(fetchTasks)
              }
            >
              <option value="To-Do">To-Do</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </p>
          <p>
            Deadline:
            <input
              type="date"
              value={task.deadline || ''}
              onChange={(e) =>
                axios.put(`http://localhost:3001/api/tasks/${task.id}`, { deadline: e.target.value }).then(fetchTasks)
              }
            />
          </p>
          <div>
            <button onClick={() => archiveTask(task.id)}>Archive</button>
            <button onClick={() => deleteTask(task.id)}>Remove</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TaskList;
