import React, { useState } from 'react';
import './CSS/Homepage.css'; // Add styles for the dashboard layout
import CreateTask from '../components/CreateTask';

function Homepage() {
    const [showCreateTask, setCreateTask] = useState(false); // Manage visibility of TaskList

    return (
      <div className="homepage-container">
        {/* Sidebar */}
        <div className="sidebar">
          <h2>Task Manager</h2>
          <div className="widget">
            <h3>Tasks</h3>
            <p>View and manage your tasks.</p>
          </div>
          <div className="widget add-task-widget" onClick={() => setShowTaskList(true)}>
            <h3>
              <span className="add-symbol">+</span> Create Task
            </h3>
            <p>Add new tasks to your list.</p>
          </div>
          <div className="widget">
            <h3>Archive</h3>
            <p>Access archived tasks.</p>
          </div>
        </div>

        {/* Main Content */}
        <div className="main-content">
          {showCreateTask ? (
            <CreateTask /> // Display TaskList if showTaskList is true
          ) : (
            <>
              <h1>Welcome to the Dashboard</h1>
              <p>Here you can manage all your tasks effectively.</p>
            </>
          )}
        </div>
      </div>
    );
}

export default Homepage;
