import React from 'react';
import './PriorityOverview.css'; // Add custom styles here

const PriorityOverview = ({ tasks }) => {
  // Count the number of tasks by priority
  const highPriorityCount = tasks.filter((task) => task.priority === 'High').length;
  const mediumPriorityCount = tasks.filter((task) => task.priority === 'Medium').length;
  const lowPriorityCount = tasks.filter((task) => task.priority === 'Low').length;

  return (
    <div className="sidebar-priority-overview">
      <div className="priority-item">
        <div className="vertical-line" style={{ backgroundColor: 'red' }}></div>
        <p>High</p>
        <h2>{highPriorityCount}</h2>
      </div>
      <div className="priority-item">
        <div className="vertical-line" style={{ backgroundColor: 'orange' }}></div>
        <p>Medium</p>
        <h2>{mediumPriorityCount}</h2>
      </div>
      <div className="priority-item">
        <div className="vertical-line" style={{ backgroundColor: 'green' }}></div>
        <p>Low</p>
        <h2>{lowPriorityCount}</h2>
      </div>
    </div>
  );
};

export default PriorityOverview;
